function ParamObject() {
    this.correctSubCats = false;
    this.jsonLocation = undefined;
    this.number = undefined;
    this.args = [];
    return this;
}

//Creates a Card Object used in <AddCard> & <RemoveCard>
function CreateCard(json, amount) {
    this.number = json["cardNumber"];
    this.amount = amount;
    this.type = parseInt(json["cardType"]);
    this.name = json["name"];
    this.json = json;
    return this;
}

module.exports = {
    ParamObject,
    CreateCard
};