function ParamObject() {
    this.correctSubCats = false;
    this.jsonLocation = "";
    this.number = "";
    this.args = [];
    return this;
}

//Creates a Card Object used in <AddCard> & <RemoveCard>
function Card(json, amount, comment) {
    this.number = json["cardNumber"];
    this.amount = amount;
    this.type = parseInt(json["cardType"]);
    this.name = json["name"];
    this.json = json;
    this.comment = comment !== undefined ? comment : "";
    return this;
}

module.exports = {
    ParamObject,
    Card
};